import React, { useState, useEffect } from 'react';
import './App.css';
import quizData from './quizData';

function App() {
  const [state, setState] = useState({
    questions: [],
    currentQuestion: 0,
    userSelections: [],
    score: 0,
    quizCompleted: false,
    timer: 30,
  });

  const { questions, currentQuestion, userSelections, score, quizCompleted, timer } = state;

  useEffect(() => {
    setState((prevState) => ({ ...prevState, questions: quizData }));
  }, []);

  useEffect(() => {
    // Timer logic
    let timerInterval;

    if (currentQuestion < questions.length && !quizCompleted) {
      timerInterval = setInterval(() => {
        if (timer > 0) {
          setState((prevState) => ({ ...prevState, timer: prevState.timer - 1 }));
        } else {
          clearInterval(timerInterval);
          // Handle time's up, e.g., move to the next question or show an alert
        }
      }, 1000);
    }

    return () => clearInterval(timerInterval);
  }, [currentQuestion, timer, questions.length, quizCompleted]);

  const handleOptionClick = (selectedOption) => {
    setState((prevState) => ({
      ...prevState,
      userSelections: [...prevState.userSelections, selectedOption],
    }));
  };

  const checkAnswer = () => {
    const currentQuestionData = questions[currentQuestion];
    const selectedAnswer = userSelections[currentQuestion];
    if (selectedAnswer === currentQuestionData.correctAnswer) {
      setState((prevState) => ({ ...prevState, score: prevState.score + 1 }));
    }
  };

  const moveToNextQuestion = () => {
    if (currentQuestion < questions.length - 1) {
      setState((prevState) => ({
        ...prevState,
        currentQuestion: prevState.currentQuestion + 1,
        timer: 30,
        userSelections: [],
      }));
    } else {
      setState((prevState) => ({ ...prevState, quizCompleted: true }));
    }
  };

  const renderQuiz = () => (
    <>
      <div className="question" style={{ opacity: currentQuestion === questions.length ? 0 : 1 }}>
        <p>{questions[currentQuestion].question}</p>
      </div>
      <div className="options">
        {questions[currentQuestion].options.map((option, index) => (
          <div key={index} className="option" onClick={() => handleOptionClick(option)}>
            {option}
          </div>
        ))}
      </div>
      <div className="timer">Time Remaining: {timer} seconds</div>
      <button onClick={checkAnswer}>Check Answer</button>
      <button onClick={moveToNextQuestion}>
        {currentQuestion === questions.length - 1 ? 'Finish Quiz' : 'Next Question'}
      </button>
    </>
  );

  return (
    <div className="App">
      <h1>Quiz App</h1>
      {quizCompleted ? (
        <div className="result">
          <h2>Quiz Completed!</h2>
          <p>Your Score: {score} / {questions.length}</p>
        </div>
      ) : (
        renderQuiz()
      )}
    </div>
  );
}

export default App;
