import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [state, setState] = useState({
    questions: [
      {
        question: "What is the capital of France?",
        options: ["London", "Madrid", "Paris", "Berlin"],
        correctAnswer: "Paris",
      },
      {
        question: "Which planet is known as the Red Planet?",
        options: ["Earth", "Mars", "Venus", "Jupiter"],
        correctAnswer: "Mars",
      },
      // Add more questions here
    ],
    currentQuestion: 0,
    userSelections: [],
    score: 0,
    quizCompleted: false,
    timer: 30,
  });

  const { questions, currentQuestion, userSelections, score, quizCompleted, timer } = state;

  useEffect(() => {
    // Your code for setting up the timer
    // ...

    // Fetch quiz data or perform other setup as needed
    // ...
  }, []);

  const handleOptionClick = (selectedOption) => {
    setState((prevState) => ({
      ...prevState,
      userSelections: [...prevState.userSelections, selectedOption],
    }));
  };

  const checkAnswer = () => {
    // Your code to check the user's answer
    // ...
  };

  const moveToNextQuestion = () => {
    // Your code to move to the next question
    // ...
  };

  const renderQuiz = () => {
    if (currentQuestion < questions.length) {
      const currentQuestionData = questions[currentQuestion];
      return (
        <div className="question">
          <p>{currentQuestionData.question}</p>
          <div className="options">
            {currentQuestionData.options.map((option, index) => (
              <div key={index} className="option" onClick={() => handleOptionClick(option)}>
                {option}
              </div>
            ))}
          </div>
          <div className="timer">Time Remaining: {timer} seconds</div>
          <button onClick={checkAnswer}>Check Answer</button>
          <button onClick={moveToNextQuestion}>
            {currentQuestion === questions.length - 1 ? 'Finish Quiz' : 'Next Question'}
          </button>
        </div>
      );
    } else {
      return (
        <div className="result">
          <h2>Quiz Completed!</h2>
          <p>Your Score: {score} / {questions.length}</p>
        </div>
      );
    }
  };

  return (
    <div className="App">
      <h1>Quiz App</h1>
      {renderQuiz()}
    </div>
  );
}

export default App;
