import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [questions, setQuestions] = useState([
    {
      question: 'What is the capital of France?',
      options: ['London', 'Berlin', 'Paris', 'Madrid'],
      correctAnswer: 'Paris',
    },
    {
      question: 'Which planet is known as the Red Planet?',
      options: ['Mars', 'Jupiter', 'Earth', 'Venus'],
      correctAnswer: 'Mars',
    },
  ]);

  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [userSelections, setUserSelections] = useState([]);
  const [score, setScore] = useState(0);
  const [quizCompleted, setQuizCompleted] = useState(false);
  const [timer, setTimer] = useState(30);

  useEffect(() => {
    const timerInterval = setInterval(() => {
      if (timer > 0) {
        setTimer(timer - 1);
      } else {
        moveToNextQuestion();
        clearInterval(timerInterval);
      }
    }, 1000);

    return () => clearInterval(timerInterval);
  }, [timer]);

  const handleOptionChange = (event) => {
    const selectedOption = event.target.value;
    setUserSelections([...userSelections, selectedOption]);
  };

  const checkAnswer = () => {
    const currentQuestionData = questions[currentQuestion];
    const selectedAnswer = userSelections[currentQuestion];

    if (selectedAnswer === currentQuestionData.correctAnswer) {
      setScore(score + 1);
    }
  };

  const moveToNextQuestion = () => {
    if (currentQuestion < questions.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
      setTimer(30);
      setUserSelections([]);
    } else {
      setQuizCompleted(true);
    }
  };

  const renderQuiz = () => {
    if (currentQuestion < questions.length) {
      return (
        <div className="question">
          <p>{questions[currentQuestion].question}</p>
          <div className="options">
            <select
              value={userSelections[currentQuestion] || ''}
              onChange={handleOptionChange}
            >
              <option value="">Select an option</option>
              {questions[currentQuestion].options.map((option, index) => (
                <option key={index} value={option}>
                  {option}
                </option>
              ))}
            </select>
          </div>
          <div className="timer">Time Remaining: {timer} seconds</div>
          <button onClick={checkAnswer}>Check Answer</button>
          <button onClick={moveToNextQuestion}>
            {currentQuestion === questions.length - 1
              ? 'Finish Quiz'
              : 'Next Question'}
          </button>
        </div>
      );
    } else {
      return (
        <div className="result">
          <h2>Quiz Completed!</h2>
          <p>Your Score: {score} / {questions.length}</p>
        </div>
      );
    }
  };

  return (
    <div className="App">
      <h1>Quiz App</h1>
      {quizCompleted ? <div className="result">{renderQuiz()}</div> : renderQuiz()}
    </div>
  );
}

export default App;
